Script.OnLoadScript:
_menu = new MenuItem("Duplicate Selected Sessions");
_menu.Shortcut = System.Windows.Forms.Shortcut.CtrlN;
_menu.Click += (sender,e)=>{
	FiddlerApplication.UI.AddImportedSessions(
		FiddlerApplication.UI.GetSelectedSessions().Select(
			p => new Fiddler.Session(
				new SessionData(p)
			)
		)
	);
};
FiddlerApplication.UI.mnuSessionContext.MenuItems.Add(_menu);
